import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import api, { authService, customerService, providerService } from '../services/api';

// Create the authentication context
const AuthContext = createContext();

// Custom hook to use the auth context
export const useAuth = () => {
  return useContext(AuthContext);
};

// Provider component
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Check if user is authenticated on initial load
  const checkAuthStatus = useCallback(async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');

      if (!token) {
        setUser(null);
        setLoading(false);
        return;
      }

      // Set default headers for all requests
      api.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // Try to get user profile based on role stored in token
      // Get user information from token
      const decodedToken = JSON.parse(atob(token.split('.')[1]));
      const role = decodedToken.role;

      let response;
      if (role === 'customer') {
        response = await customerService.getProfile();
      } else if (role === 'provider') {
        response = await providerService.getProfile();
      } else {
        throw new Error('Invalid user role');
      }

      if (response && response.data) {
        setUser({
          ...response.data,
          role
        });
      } else {
        setUser(null);
        localStorage.removeItem('token');
      }
    } catch (err) {
      console.error('Auth status error:', err);
      setUser(null);
      localStorage.removeItem('token');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    checkAuthStatus();
  }, [checkAuthStatus]);


  // Login function
  const login = async ({ id, password }) => {
    setLoading(true);
    try {
      const res = await axios.post(`${API_BASE_URL}/auth/login`, { id, password });
      const { token, user } = res.data;

      // Save to localStorage
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(user));

      setUser(user);
      setToken(token);
      setError(null);
      return { success: true };
    } catch (err) {
      setError(err.response?.data?.error || 'Login failed');
      return { success: false };
    } finally {
      setLoading(false);
    }
  };


  // Register function for customers
  const registerCustomer = async (userData) => {
    setLoading(true);
    setError('');
    try {
      // Transform the frontend form data to match backend expectations
      const backendData = {
        name: `${userData.firstName} ${userData.lastName}`,
        email: userData.email,
        phone: userData.phoneNumber,
        password: userData.password, // Add password to registration data
        visibility: 'public' // Default visibility
      };

      const response = await authService.registerCustomer(backendData);

      const { token, user } = response.data;

      // Don't save token on registration - require explicit login

      return { success: true, userId: user.id };
    } catch (err) {
      console.error('Customer registration error:', err);
      setError(err.response?.data?.error || 'Failed to register. Please try again.');
      return { success: false, error: err.response?.data?.error || 'Registration failed' };
    } finally {
      setLoading(false);
    }
  };

  // Register function for providers
  const registerProvider = async (userData) => {
    setLoading(true);
    setError('');
    try {
      // Transform the frontend form data to match backend expectations
      const backendData = {
        name: userData.businessName,
        email: userData.email,
        phone: userData.phoneNumber,
        password: userData.password, // Add password to registration data
        transportMode: mapServiceTypeToTransportMode(userData.serviceType)
      };

      const response = await authService.registerProvider(backendData);

      const { token, user } = response.data;

      // Don't save token on registration - require explicit login

      return { success: true, userId: user.id };
    } catch (err) {
      console.error('Provider registration error:', err);
      setError(err.response?.data?.error || 'Failed to register. Please try again.');
      return { success: false, error: err.response?.data?.error || 'Registration failed' };
    } finally {
      setLoading(false);
    }
  };

  // Helper function to map service type to transport mode
  const mapServiceTypeToTransportMode = (serviceType) => {
    switch (serviceType) {
      case 'Hotel':
        return 'land';
      case 'Transportation':
        return 'land';
      case 'Tour Guide':
        return 'land';
      case 'Restaurant':
        return 'land';
      case 'Activity':
        return 'land';
      default:
        return 'land'; // Default to land
    }
  };

  // Register function that calls the appropriate registration function based on role
  const register = async (userData) => {
    if (userData.role === 'customer') {
      return registerCustomer(userData);
    } else if (userData.role === 'provider') {
      return registerProvider(userData);
    } else {
      setError('Invalid user role');
      return { success: false, error: 'Invalid user role' };
    }
  };

  // Logout function
  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
    // Remove authorization header
    delete api.defaults.headers.common['Authorization'];
  };

  // Update profile function
  const updateProfile = async (userData) => {
    setLoading(true);
    setError('');
    try {
      let response;

      if (user.role === 'customer') {
        // For customer profile update
        const updateData = {
          name: userData.firstName && userData.lastName ? 
            `${userData.firstName} ${userData.lastName}` : undefined,
          email: userData.email,
          phone: userData.phoneNumber,
          visibility: userData.visibility || 'public'
        };

        // Only include password if it's being changed
        if (userData.password) {
          updateData.password = userData.password;
        }

        response = await customerService.updateProfile(updateData);
      } else if (user.role === 'provider') {
        // For provider profile update
        const updateData = {
          name: userData.businessName,
          email: userData.email,
          phone: userData.phoneNumber,
          transportMode: userData.transportMode || 'land'
        };

        // Only include password if it's being changed
        if (userData.password) {
          updateData.password = userData.password;
        }

        response = await providerService.updateProfile(updateData);
      }

      // Update local user state with new data
      setUser(prev => ({
        ...prev,
        ...(response?.data || {})
      }));

      return { success: true };
    } catch (err) {
      console.error('Update profile error:', err);
      setError(err.response?.data?.error || 'Failed to update profile. Please try again.');
      return { success: false, error: err.response?.data?.error || 'Profile update failed' };
    } finally {
      setLoading(false);
    }
  };

  // Change password function
  const changePassword = async (oldPassword, newPassword) => {
    setLoading(true);
    setError('');
    try {
      let response;

      if (user.role === 'customer') {
        response = await customerService.changePassword(oldPassword, newPassword);
      } else if (user.role === 'provider') {
        response = await providerService.changePassword(oldPassword, newPassword);
      } else {
        throw new Error('Invalid user role');
      }

      return { success: true };
    } catch (err) {
      console.error('Password change error:', err);
      setError(err.response?.data?.error || 'Failed to change password. Please try again.');
      return { success: false, error: err.response?.data?.error || 'Password change failed' };
    } finally {
      setLoading(false);
    }
  };

  // Clear error function
  const clearError = () => {
    setError('');
  };

  // Context value
  const value = {
    user,
    loading,
    error,
    checkAuthStatus,
    login,
    register,
    logout,
    updateProfile,
    changePassword,
    clearError
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
