import React, { useEffect } from 'react';
import { Navigate, Outlet, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { Box, CircularProgress, Typography } from '@mui/material';

/**
 * ProtectedRoute component provides route protection based on authentication status.
 * It can be used to protect routes that require user authentication, 
 * and optionally specific user roles.
 * 
 * @param {Object} props - Component props
 * @param {string[]} [props.allowedRoles] - Optional array of roles allowed to access the route
 * @returns {JSX.Element} - Rendered component
 */
const ProtectedRoute = ({ allowedRoles }) => {
  const { user, loading, checkAuthStatus } = useAuth();
  const location = useLocation();

  useEffect(() => {
    // Check authentication status when component mounts
    checkAuthStatus();
  }, [checkAuthStatus]);

  // Show loading indicator while checking authentication
  if (loading) {
    return (
      <Box 
        sx={{ 
          display: 'flex', 
          flexDirection: 'column',
          alignItems: 'center', 
          justifyContent: 'center', 
          height: '100vh' 
        }}
      >
        <CircularProgress size={60} thickness={4} />
        <Typography variant="h6" sx={{ mt: 2 }}>
          Verifying authentication...
        </Typography>
      </Box>
    );
  }

  // If user is not authenticated, redirect to login
  if (!user) {
    // Save the location the user was trying to access for redirect after login
    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  // If route has role restrictions, check if user has required role
  if (allowedRoles && !allowedRoles.includes(user.role)) {
    // User doesn't have required role, redirect to dashboard
    return <Navigate to="/dashboard" replace />;
  }

  // User is authenticated and has required role (if specified)
  return <Outlet />;
};

export default ProtectedRoute;
